import React, { Component } from 'react';
import { BrowserRouter as Link } from "react-router-dom";
import axios from 'axios';

import CreateTray from "./create-tray.component";
// import "bootstrap/dist/css/bootstrap.min.css";

const Server = "http://192.168.1.165:1337/";

var Plant = props => (
  <tr>
    <td>{props.plant.plant_species}</td>
    <td>{props.plant.num_of_pots}</td>
    <td>{props.plant.seed_pot}</td>
    <td>{props.plant.sow_date}</td>
    <td>{props.plant.stepUp}</td>
    <td>{props.plant.condition}</td>
    <td><Link to={"/edit/" + props.tray._id}>Edit</Link></td>
    <td></td>
  </tr>
)

export default class PlantList extends Component {
  constructor(props) {
    super(props);
      this.state = {plants: []};
  }

  componentDidMount() {
    var serverLocation = Server + 'plants/';
    axios.get(serverLocation)
      .then(res => {
        this.setState({ plants: res.data });
      })
      .catch(function (error){
        console.log(error);
      });
  }

  // handleDelete= () => {
  //   axios.post('http://localhost/trays/remove'+this.props.match.params.id)
  //     .then(res => console.log(res.data));
  // };

  plant_List() {
    return this.state.plants.map(function(currentPlant, plant) {
      return <Plant plant={currentPlant} key={plant._id} />
    })
  }

  render() {
    return (
      <div className="container">
      // <div className="row">
      // <div className="col">
        <h3 >Plant List</h3>
        <Link to="/create">Add plants</Link>
        <table className="table table-striped" style={{ marginTop: 20 }} >
          <thead>
            <tr>
              <th>Plant Species</th>
              <th># of Pots</th>
              <th>Seeds/Pot</th>
              <th>Sowing date</th>
              <th>Stepped Up</th>
              <th>Condition</th>
              <th></th>
              <th><button>Remove</button></th>
            </tr>
          </thead>
          <tbody>
            { this.plant_List() }
          </tbody>
        </table>
    </div>
    // <div className="col">
      // <CreateTray/>
    // </div>
    </div>

      </div>
    )
  }
}
