import React, { Component } from 'react';
import { BrowserRouter as  Link } from "react-router-dom";
import axios from 'axios';

import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';

import CreateTray from "./create-tray.component";
// import "bootstrap/dist/css/bootstrap.min.css";

const Server = "http://192.168.1.165:1337/";


export default class PlantList extends Component {
  constructor(props) {
    super(props);
      this.state = {plants: []};
  }

  componentDidMount() {
    var serverLocation = Server + 'plants/';
    axios.get(serverLocation)
      .then(res => {
        this.setState({ plants: res.data });
      })
      .catch(function (error){
        console.log(error);
      });
  }



  // handleDelete= () => {
  //   axios.post('http://localhost/trays/remove'+this.props.match.params.id)
  //     .then(res => console.log(res.data));
  // };

  plant_List() {
    return this.state.plants.map(
      function(plant) {
        return (
          <tr>
            <td>{plant.plant_species}</td>
            <td>{plant.num_of_pots}</td>
            <td>{plant.seed_pot}</td>
            <td>{plant.sow_date}</td>
            <td>{plant.stepUp}</td>
            <td>{plant.condition}</td>
            <td><Link to={"/edit/" + plant._id}>Edit</Link></td>
            <td><button onClick={plant => axios.delete(Server + 'plants/remove' + plant._id)
            .then(res => console.log(res.data))
            }>Remove</button></td>
            <td></td>
          </tr>
        )
    })
  }

  render() {
    return (

      <Container>
      <Row>
      <Col sm={8}>
        <h3 >Plant List</h3>
        <table className="table table-striped" style={{ marginTop: 20 }} >
          <thead>
            <tr>
              <th>Plant Species</th>
              <th># of Pots</th>
              <th>Seeds/Pot</th>
              <th>Sowing date</th>
              <th>Stepped Up</th>
              <th>Condition</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            { this.plant_List() }
          </tbody>
        </table>
        <br/>
      </Col>
      <Col sm={4}>
          <CreateTray/>
      </Col>
      </Row>
      </Container>
    )
  }
}
